import React , { useState, useEffect } from 'react';
import { connect } from "react-redux";
import * as actions from "../../redux/actions/actions";
import { PaginationWrapper, PaginationBtn } from "./PaginationStyled";


const nextButtonValue = "Next";
const previousButtonValue = "Previous";

const Pagination = ({  props, gender, page, updateGlobalState, maxPages}) => {

  console.log(gender)
  console.log(props.gnomes)

  useEffect(() => {
    console.log("calling again")
    props.fetchGender(format).then(response =>{ JSON.stringify(response); console.log(response)})

    console.log("gender x")
    console.log(gender)

  }, [])

  console.log("AFTER")
  console.log(gender)

  const gnomeNames = gnomes.map(gnome => gnome.name)
  const pageGnomes = gnomeNames.slice(
  itemsByPage * currentPage,
  itemsByPage + currentPage * itemsByPage
  ) // slice will control the pagination it calculate the quantity of items we want to show by the current page

  pageGnomes.map(gnome => (
    gnome => gnome.name
  ))

  const getFirstName = arr =>{
    var arrCroped = [];
      for(var a=0; a<arr.length; a++){

        var corte = arr[a].indexOf(" ");
        arrCroped[a] = arr[a].substr(0, corte);
      }
      return arrCroped;
  }

  const justRenderedPageNames = getFirstName(pageGnomes);

  const parseToService = arrToFormat =>{
    var arrayFormated = [];

      for(var b=0; b<arrToFormat.length; b++){
        arrayFormated[b] = "name[]="+arrToFormat[b];
      }

      return arrayFormated;

  }

  const format = parseToService(justRenderedPageNames).toString().replace(/,/g, "&");


  return(
  <PaginationWrapper>
    <PaginationBtn
      disableStyle={page <= 0}
      page={page}
      onClick={Pagination.onClick(updateGlobalState, page)}
    >
      Previous
    </PaginationBtn>
    <span className="current-page">Page: {page + 1}</span>
    <PaginationBtn
      disableStyle={page >= maxPages}
      page={page}
      onClick={Pagination.onClick(updateGlobalState, page, maxPages)}
    >
      Next
    </PaginationBtn>
  </PaginationWrapper>
)};

Pagination.onClick = (updateGlobalState, page, maxPages) => (
  event= event.currentTarget
) => {
  event.preventDefault();
  // when page change take scroll to top
  window.scroll(null, 0);
  console.log(gender)
  //callGender(propsGender)
  // Get the button name
  const btnName = event.currentTarget.textContent.toLowerCase();

  // if btnName is equal tot the nextButton value add a page until find the last page
  if (btnName === nextButtonValue.toLowerCase()) {
    // just add a new page if it is minor than maxPage
    const nextPage = page + 1;
    if (page !== maxPages) {
      updateGlobalState(nextPage);
    }
  } else if (btnName === previousButtonValue.toLowerCase()) {
    // Remove a page until it find the first page
    const previousPage = page - 1;
    if (page > 0) {
      updateGlobalState(previousPage);
    }
  }
};

Pagination.defaultProps = {
  page: false
};

function mapStateToProps(state) {
  console.log("pagination gender")
  console.log(state)
  return {
    gnomes: state.gnomes,
    gender: state.gender
  };
}

export default connect(mapStateToProps, actions)(Pagination);

