import React, { useState, useEffect } from 'react';
import { Helmet } from "react-helmet";
import { Transition, CSSTransition, TransitionGroup } from "react-transition-group";
import { connect } from "react-redux";
import Filters from "./../Filters/Filters";
import Card from "./../../components/Card/Card";
import * as actions from "./../../redux/actions/actions";
import { HomeRow, HomeWrapper } from "./HomeStyled";
import Pagination from "./../../components/Pagination/Pagination";
import { filterGnomesBy } from "./../../utils";


const Home = (props) => {

  console.log("props")
  console.log(props.store)
  const { searchTerm, gnomes, orderByFilter, gender, currentPage, direction } = props;

  //const [gender, setGender] = useState([]);

  useEffect(() => {
    console.log("calling again")
    console.log(format)
    //props.fetchGender(format).then(response =>{ JSON.stringify(response); console.log(response)})

    console.log("gender x")
    console.log(gender)

  }, [])

  console.log("AFTER")
  console.log(gender)

  const referenceFetchGender = props.fetchGender;
  const itemsByPage = 1;

  //if(currentPage != 1){
    //currentPage = props.getCurrentPage;
    console.log("props.getCurrentPage")
    console.log(props.getCurrentPage(currentPage))
    console.log("DIRECTION")
    console.log(props.direction)
  //}
  const updatedPageNext = props.getCurrentPage(currentPage).payload + 1;
  const updatedPagePrevious = props.getCurrentPage(currentPage).payload  - 1;

  let paginationDirection = 0;
  if(props.direction != ""){
    paginationDirection = props.direction == "next" ? updatedPageNext : updatedPagePrevious;
    props.fetchGender(format).then(response =>{ JSON.stringify(response); console.log(response)})
  }else{
    paginationDirection = currentPage
    props.fetchGender(format).then(response =>{ JSON.stringify(response); console.log(response)})
  }

  const gnomeNames = gnomes.map(gnome => gnome.name)
  const pageGnomes = gnomeNames.slice(
  itemsByPage * paginationDirection,
  itemsByPage + paginationDirection * itemsByPage
  ) // slice will control the pagination it calculate the quantity of items we want to show by the current page

  pageGnomes.map(gnome => (
    gnome => gnome.name
  ))

  const getFirstName = arr =>{
    var arrCroped = [];
      for(var a=0; a<arr.length; a++){

        var corte = arr[a].indexOf(" ");
        arrCroped[a] = arr[a].substr(0, corte);
      }
      return arrCroped;
  }

  const justRenderedPageNames = getFirstName(pageGnomes);

  const parseToService = arrToFormat =>{
    var arrayFormated = [];

      for(var b=0; b<arrToFormat.length; b++){
        arrayFormated[b] = "name[]="+arrToFormat[b];
      }

      return arrayFormated;

  }

  const format = parseToService(justRenderedPageNames).toString().replace(/,/g, "&");


  // Filter the gnomes ordering them by youngest, oldest, lighter, heaviest, popularity
  // Returns and array with all the filtered gnomes and finally generate
  // our card based this filtered array
  const searchFilters = filterGnomesBy(gnomes, orderByFilter)
    .filter(gnome => {
      // filter by profession
      // If this gnome has this profession from redux state
      // return the profession of this gnome
      const isJob = gnome.professions.find(
        profession => profession === props.filterBy
      );

      // And then if this gnome know this profession return true
      // Otherwise return false and ignore pass this gnome to the new array
      const hasProfession = gnome.professions.some(
        profession => profession === isJob
      );
      // if filterBy state is all returns all gnomes if not return depending
      // of the profession we're looking for
      return props.filterBy === "All" ? gnome : hasProfession;
    })
    .filter(gnome => {
      // Filter name
      // This comentted expression is for search exactly
      // if match from the beginning of the string
      // gnome.name.toLowerCase().search(searchTerm.toLowerCase()) === 0

      // Get the gnomes that contains this bit of string within
      const search =
        gnome.name.toLowerCase().indexOf(searchTerm.toLowerCase()) >= 0;
      return search;
    });

  return (
    <HomeWrapper>
      <Helmet>
        <title>Nicolas Bergues</title>
        <link rel="shortcut icon" href="favicon.ico" />
        <meta
          name="description"
          content="Assesment App"
        />
      </Helmet>
      <HomeRow>
        <Filters />
      </HomeRow>
      <Pagination
        page={currentPage}
        updateGlobalState={props.getCurrentPage}
        maxPages={Math.floor(searchFilters.length / itemsByPage)}
        propsGender={format}
        callGender={referenceFetchGender}
        updateDirection={props.getDirection}
      />
      <HomeRow>
      <TransitionGroup className="cards">
        {searchFilters.length > 0 ? (
          searchFilters
            .slice(
              itemsByPage * currentPage,
              itemsByPage + currentPage * itemsByPage
            )
            .map(gnome => (
              <CSSTransition key={gnome.id} timeout={500} classNames="item">
                <Card
                  key={gnome.id}
                  gnome={gnome && gnome}
                  to={`${gnome.id}-${gnome.name.split(" ").join("-")}`}
                />
              </CSSTransition>
            ))
        ) : (
          <div className={"notFound"}>no gnomes founded for current search</div>
        )}
        </TransitionGroup>
      </HomeRow>
      <Pagination
        page={currentPage}
        updateGlobalState={props.getCurrentPage}
        maxPages={Math.floor(searchFilters.length / itemsByPage)}
        propsGender={format}
        callGender={referenceFetchGender}
        updateDirection={props.getDirection}
      />
    </HomeWrapper>
  );
};


function mapStateToProps(state) {
  console.log("state")
  console.log(state)
  return {
    searchTerm: state.searchTerm,
    gnomes: state.gnomes,
    gender: state.gender,
    filterBy: state.filterBy,
    orderByFilter: state.orderBy,
    currentPage: state.currentPage,
    direction: state.direction
  };
}

export default connect(mapStateToProps, actions)(Home);
