import styled from "styled-components";

const LoadingStyled = styled.div`
  margin-left: auto;
  margin-right: auto;
  height: 100vh;
  width: 100vw;
  display: flex;
  justify-content: center;
  align-items: center;
  background: #fff;

    h1 {
      font-size: 2.3rem;
      margin-left: auto;
      margin-right: auto;
    }
    span {
      font-size: 1.4rem;
    }

  @media screen and (min-width: 400px) {
    & > div.box {
      h1 {
        font-size: 1.5rem;
      }
      span {
        font-size: 1.0rem;
      }
    }
  }
`;

export default LoadingStyled;
