import React, {Component, useEffect, useState } from 'react';
import { connect } from "react-redux";
import * as actions from "../../redux/actions/actions";
import { convertColors } from "./../../utils";
import  GenderCard  from "./GenderCard/GenderCard";
import { GnomeCard, Avatar, GnomeInfo, GnomeInfoList } from "./GenderStyled";

class Gender extends Component {

  name = "";

  constructor(props){
    super(props)
  }

  componentDidMount(){
    const cut = this.props.gnome.name.indexOf(" ");
    name = this.props.gnome.name.substr(0, cut);
    this.props.fetchGender( "name[]="+ name );
  }

  //const { to,  gnome} = props;


  /*
  const[gender, setGender] = useState([]);

    useEffect(() => {

      props.fetchGender( "name[]="+ name ).then(response =>{
        setGender(response)
      })
    }, []);

    useEffect(() => {
       if (gender.length > 0) {
            console.log("HAY DATA")
            console.log(gender[0])
            //renderGenderData(gender);
       }
    },[gender]);

*/
    //console.log(gender)

    renderGenderData = (genderObj) => {
      console.log(genderObj)
      console.log(this.props.gender)


      if(this.props.gender.lenght > 0)
          return(
            <div>{`${this.name} is a ${genderObj[0].gender} !`}</div>
          )
      console.log(genderObj)
      console.log(this.props.gender)


    }


    render(){
  return(

  <GnomeCard to={this.props.to}>
    <div className="banner" />

    <Avatar src={this.props.gnome && this.props.gnome.thumbnail}>
      <img
        src={this.props.gnome && this.props.gnome.thumbnail}
        alt={`avatar ${this.props.gnome && this.props.gnome.name}`}
      />
    </Avatar>
    <header className="description">
      <h2>{this.props.gnome && this.props.gnome.name}</h2>
      <div>
        {this.props.gnome.professions.length > 0
          ? this.props.gnome.professions.join(" â€¢ ")
          : "No Profession"}
      </div>
    </header>

    <GnomeInfo>
    <GenderCard gender={this.props.gender}/>
    {this.renderGenderData(this.props.gender)}
    {this.props.gender && this.props.gender.lenght > 0 ?  <div>{`${this.name} is a ${this.props.gender[0].gender} !`}</div> : <div>AHHHHHHHHHHHH</div>}
      <GnomeInfoList hairColor={this.props.gnome && convertColors(this.props.gnome.hair_color)}>
        <span>Age</span>
        <span>{this.props.gnome && this.props.gnome.age}</span>
      </GnomeInfoList>
      <GnomeInfoList hairColor={this.props.gnome && convertColors(this.props.gnome.hair_color)}>
        <span>Weight</span>
        <span>
          {this.props.gnome && Math.ceil(this.props.gnome.weight)} <span className="unit">KG</span>
        </span>
      </GnomeInfoList>
      <GnomeInfoList hairColor={this.props.gnome && convertColors(this.props.gnome.hair_color)}>
        <span>Height</span>
        <span>
          {this.props.gnome && Math.ceil(this.props.gnome.height)} <span className="unit">CM</span>
        </span>
      </GnomeInfoList>
    </GnomeInfo>
    <div className="banner-footer" />
  </GnomeCard>

)}
};

const mapStateToProps = state => ({
  gender: state.gender
});

export default connect(mapStateToProps, actions)(Gender);
