// @flow
import React, { Component } from "react";
import { connect } from "react-redux";
import Link from 'next/link';
//import { BrowserRouter as Router } from "react-router-dom";
import * as actions from "../redux/actions/actions";
import Loading from "../components/Loading/Loading";
import Home from './Home/Home.js'
import Routes from "./../../routes/routes";
//import Details from "./../containers/Details/Details.js";
import Header from "./Header/Header.js";
import Footer from "./../components/Footer/Footer.js";
//import dynamic from 'next/dynamic';

// Declaring Flow types for Props
/*type Props = {
  fetchGnomes: Function,
  gnomes: Array<Gnome>
};*/

// now pass the Types declaration to our class component
class Main extends Component {
  componentDidMount() {
    console.log("mount")
    // Get all the gnomes from the Api
    // this is an action creator we got from the react-redux connect method
    this.props.fetchGnomes();
    this.props.fetchGender("name[]=Tobus&name[]=Fizkin&name[]=Malbin&name[]=Midwig&name[]=Malbin&name[]=Zedkin&name[]=Emmadette&name[]=Twizzle&name[]=Malbert&name[]=Kinthony&name[]=Cogwitz&name[]=Malbert&name[]=Ecki&name[]=Tobus&name[]=Emmadette");
  }

  // If there is not gnomes in the gnomes state (got from Redux state)
  // it will render Loading component first while we can receive all the inhabitants
  render() {//"/gnomes/297-Malbert-Wrongtink">
    return (
      <div>
      {this.props.gnomes.length === 0 ? (
        <Loading />
      ) : (
        <div>

        <Header />
        <Home data={this.props} store={this.props.store}/>

        <Footer />


        </div>

      )}
      </div>
      //<Details gnomes={this.props.gnomes} gnomeURL="0-Tobus-Quickwhistle" />
      /*<Router>

      </Router>*/
      /*<DynamicComponent gnomes={this.props.gnomes} gnomeURL="0-Tobus-Quickwhistle"/>
      <Routes gnomes={this.props.gnomes}>

      </Routes>*/

    );
  }
}

// mapStateToProps we pass this function to the connect method to
// inject our Redux states as props in Main container
//const DynamicComponent = dynamic(() => import('./../containers/Details/Details.js'))

function mapStateToProps(state) {
  return {
    gnomes: state.gnomes,
    gender: state.gender
  };
}

export default connect(mapStateToProps, actions)(Main);
