import React from "react";
import styled from "styled-components";

export const ModalStyle = styled.div`

    button {
      left: 31%;
      top: -12px;
      padding-bottom: 5px;
      display: flex;
      align-items: center;
      position: relative;
      cursor: pointer;
      background: transparent;
      font-weight: lighter;
      font-size: 0.7rem;
      font-color: #ccc;
    }

    div.modal {
      font-size: 12px;
    }
    div.header {
      width: 100%;
      border-bottom: 1px solid gray;
      font-size: 18px;
      text-align: center;
      padding: 5px;
    }
    div.content {
      width: 100%;
      padding: 10px 5px;
    }
    div.actions {
      width: 100%;
      padding: 10px 5px;
      margin: auto;
      text-align: center;
    }
    a.close {
      cursor: pointer;
      position: absolute;
      display: block;
      padding: 2px 5px;
      line-height: 20px;
      right: 5px;
      top: 5px;
      font-size: 24px;
      background: #ffffff;
      border-radius: 18px;
      border: 1px solid #cfcece;
    }`;
