import React from "react";
import Popup from "reactjs-popup";
import Details from "./../../containers/Details/Details.js"
import { ModalStyle } from "./ModalStyles";
export default ({gnomes, to, isOpen}) => (
  <ModalStyle>
  <Popup className="popup" trigger={isOpen == false ? <button className="button">View Friends</button> : null} modal >

    {close => {
      isOpen = true;
      console.log("isOpen ", isOpen)
      return(
      <div  disabled className="modal">
        <a className="close" onClick={close}>
          &times;
        </a>
        <div className="header"> {to.replace(/-/g, ' ').replace(/[0-9]/g, '')} Friends </div>
        <div className="content">
          <Details gnomeURL={to}  gnomes={gnomes} />
        </div>
        <div className="actions">
          <Popup
            trigger={<button className="button"> Trigger </button>}
            position="top center"
            closeOnDocumentClick
          >
            <span>
              Lorem ipsum dolor sit amet, consectetur adipisicing elit. Beatae
              magni omnis delectus nemo, maxime molestiae dolorem numquam
              mollitia, voluptate ea, accusamus excepturi deleniti ratione
              sapiente! Laudantium, aperiam doloribus. Odit, aut.
            </span>
          </Popup>
          {/*<button
            className="button"
            onClick={() => {
              console.log("modal closed ");
              close();
            }}
          >
            close modal
          </button>*/}
        </div>
      </div>
    )}}
  </Popup>
  </ModalStyle>
);
