import { CURRENT_PAGE } from "./../actions/types";
import { DIRECTION } from "./../actions/types";

const initialState = {

    currentPage: 0,
    direction: ""

};


function paginationReducer(state = initialState, action) {
  console.log("????????")
  console.log(state)
  switch (action.type) {
    case CURRENT_PAGE:
    return Object.assign( {}, state, {
     ...state, currentPage: action.payload
   });
    case DIRECTION:
    return Object.assign({}, state, {
    ...state, direction: action.payload
   });
    default:
      return state;
  }
}
export default paginationReducer;
