// @flow
import React from "react";
import styled from "styled-components";
//import { Link } from "react-router-dom";
import Link from 'next/link';

/*type Props = {
  className: string,
  to: string,
  children: ReactElements
};*/

const CardLink = ({ className, children, to }) => (


  <div>
    <div>
        {children}
    </div>
    <div>
    <Link href={to} to={to} className={className} queryParams={{ utm_campaign: 'website' }}>
      <p> ver gnomo</p>
    </Link>
    </div>
  </div>

);
/*<Link className={className} to={to} href={to}>
  {children}
</Link>*/
//styled(CardLink)` <-- era
