import React, { Component } from "react";
import { connect } from "react-redux";
import Link from 'next/link';
//import * as actions from "../redux/actions/actions";
import * as actions from "../../src/actions/actions";
import Loader from "../components/Loader/Loader.js";
import Home from './Home/Home.js'
import Header from "./Header/Header.js";
import Footer from "./../components/Footer/Footer.js";

class Main extends Component {

  gnomes;
  fetchGnomes;

  constructor({gnomes, fetchGnomes}){
    super({gnomes, fetchGnomes});
    this.state = {};

    this.gnomes = gnomes;
    this.fetchGnomes = fetchGnomes;;
  }

  componentDidMount() {
    //prevent ugly glow on button selected
    document.addEventListener('keydown', this.handleFirstTab);
    document.addEventListener('click', this.handleClick);

    this.fetchGnomes();
    console.log(this.gnomes)
      console.log(this.props)
  }

  handleFirstTab = (e) => {
      if (e.keyCode === 9) {
          document.body.classList.add('user-is-tabbing');
          document.removeEventListener('keydown', this.handleFirstTab);
      }
  }
  handleClick= (e) => {
          document.body.classList.remove('user-is-tabbing');
          document.addEventListener('keydown', this.handleFirstTab);
  }

  render() {
    return (
      <div>
      {this.gnomes.length === 0 ? (
        <Loader />
      ) : (
        <div>

        <Header />
        <button onClick={this.fetchGnomes}>INCREMENT</button>
        <Home data={this.gnomes}/>{/*store={this.props.store}*/}
        <Footer />

        </div>
      )}
      </div>

    );
  }
}

/*function mapStateToProps(state) {
  return {
    gnomes: state.gnomes
  };
}*/

const mapStateToProps = state => ({
  gnomes: state.gnomes
});

/*const mapDispatchToProps = dispatch => {
  return {
    increment: () => dispatch(increment()),
    decrement: () => dispatch(decrement()),
    reset: () => dispatch(reset())
  };
};*/

const mapDispatchToProps = dispatch => {
  return {
    fetchGnomes: () => dispatch(actions.fetchGnomes())
  };
};

export default connect(
  mapStateToProps,
  mapDispatchToProps
)(Main);

