import styled from "styled-components";

// themes value received thenaks to themesProvider
// so themes variables are available in all styled components
const GenderCardStyle = styled.header`
  align-items: center;
  background: #146e7d;
  box-shadow: 0px 0px 5px rgba(0, 0, 0, 0.9);
  display: flex;
  height: 65px;
  justify-content: center;

  h1 {

      color: #eceeee;
      text-decoration: none;
      letter-spacing: 10px;
      font-size: 1.3em;
  }

  div.row {
    align-items: center;
    display: flex;
    justify-content: center;
    padding: 0 5px;
    max-width: 300px;
    width: 100%;
  }
`;

export default GenderCardStyle;
