import styled from "styled-components";

export const ItemSuggestions = styled.div`
  background: ${({ theme }) => theme.suggestionsBG};
  border: 1px solid #ccc;
  border-radius: 0 0 5px 5px;
  box-sizing: border-box;
  color: ${({ theme }) => theme.suggestionsTXT};
  display: ${props => (props.open ? "block" : "none")};
  font-family: Helvetica, sans-serif;
  left: 0;
  max-width: ${props => props.maxWidth};
  position: absolute;
  width: 100%;
  z-index: 2;

  & > ul.suggestions-list {
    list-style: none;
    margin: 0;
    padding: 0;
  }
`;

export const Suggestion = styled.li`
  background-color: ${props => (props.active ? `#b9e5ed` : "transparent")};
  color: ${props =>
    props.active ? `#fff` : `${({ theme }) => theme.suggestionsTXT}`};
  cursor: pointer;
  padding: 10px 15px;
`;
