    dispatch(getGender(gender));
  } catch (err) {
    throw new Error(err);
  }
};

export const getGender = (gender) => ({
  type: REQUEST_GENDER,
  payload: gender
});

/* eslint-enable */
export const getSearchText = (searchTerm) => ({
  type: SEARCH_TERM,
  payload: searchTerm
});

export const filterByJob = (job) => ({
  type: FILTER_BY_JOB,
  payload: job
});

export const updateOrderBy = (order) => ({
  type: ORDER_BY_FILTER,
  payload: order
});

export const getCurrentPage = (page) => ({
  type: CURRENT_PAGE,
  payload: page
});


