// @flow
import React from "react";
import styled from "styled-components";
//import { Link } from "react-router-dom";
import Link from 'next/link';

/*type Props = {
  className: string,
  to: string,
  children: ReactElements
};*/

const CardLink = ({ className, children, to }) => (


  <div>
    <div>
        {children}
    </div>
    <div>
    <Link href={to} to={to} className={className} queryParams={{ utm_campaign: 'website' }}>
      <p> ver gnomo</p>
    </Link>
    </div>
  </div>

);
/*<Link className={className} to={to} href={to}>
  {children}
</Link>*/
//styled(CardLink)` <-- era
export const GnomeCard = styled.div`
  border-radius: 5px;
  background: ${({ theme }) => theme.cardBG};
  box-shadow: 0px 0px 3px rgba(0, 0, 0, 0.2);
  box-sizing: border-box;
  color: ${({ theme }) => theme.mainFontColor};
  display: flex;
  flex-direction: column;
  justify-content: center;
  margin: 0 5px 15px;
  max-width: 400px;
  position: relative;
  overflow: hidden;
  text-decoration: none;
  transition: 0.5s;
  width: 100%;

  &:hover {
    box-shadow: 0px 0px 6px rgba(0, 0, 0, 0.4);
    transition: 0.5s;

    .fond{position:absolute;padding-top:85px;top:0;left:0; right:0;bottom:0;
  background-color:#65C6BB;}

.carreaux_presentation_light
{
  display:table-cell;
  width:250px; height:250px;
  background-size:cover;
  overflow: hidden;
  position: relative;
}
.carreaux_presentation_light .shadow_swhow_mini
{
  background-color:rgba(19,30,33,0.4);
  position: absolute;
  z-index: 1000;
  top:0;left:0;right:0;bottom:0;

}
.carreaux_presentation_light:hover .shadow_swhow_mini
{
  background-color:rgba(16,23,41,0);
}
.carreaux_presentation_light .deroul_titre
{
  position: absolute;
  text-transform: uppercase;
  font-family: 'Roboto';
  padding: 7px;
  z-index: 1000;
  top:60%;left:-80%;
  color:#ffffff;
  font-weight: 300;
  font-size: 13px; width: 80%;
  -webkit-transition: all 0.7s;
  -moz-transition: all 0.7s;
  -ms-transition: all 0.7s;
  transition: all 0.7s;

}
.carreaux_presentation_light:hover .deroul_titre
{
  background-color:#1e4944; left:0px;

}
.carreaux_presentation_light .deroul_soustitre
{
  position: absolute;
  font-family: 'Roboto';
  padding: 5px;
  z-index: 1000;
  top:60%;right:-85%;
  margin-top: 25px;
  color:#ffffff;
  font-weight: 300;
  font-size: 11px; width: 80%;
  -webkit-transition: all 0.7s;
  -moz-transition: all 0.7s;
  -ms-transition: all 0.7s;
  transition: all 0.7s;
}
.carreaux_presentation_light:hover .deroul_soustitre
{
  background-color:#417c74;right:0px;
}
  }

  div.banner {
    background: ${props => props.hairColor} !important;
    height: 80px;
    position: absolute;
    top: 0;
    width: 100%;

  }

  header.description {
    margin: 10px 0 20px;
    padding: 0 10px;
    text-align: center;

    h2 {
      margin: 0 0 10px;
    }
    & > div {
      font-size: 0.8rem;
    }
  }

  div.banner-footer {
    bottom: 0;
    background: ${props => props.hairColor};
    height: 8px;
    position: absolute;
    width: 100%;
  }

  @media screen and (min-width: 630px) {
    max-width: 160px;
  }
`;

export const Avatar = styled.div`
  background: ${props => `url(${props.src}) no-repeat center`};
  background-size: cover;
  border: 4px solid #fff;
  border-radius: 50%;
  box-sizing: border-box;
  margin: 30px auto 0;
  padding: 5px;
  height: 100px;
  position: relative;
  width: 100px;
  z-index: 1;
  img {
    position: absolute;
    height: 0;
    visibility: hidden;
    width: 0;
  }
`;

export const GnomeInfo = styled.ul`
  display: flex;
  list-style: none;
  padding: 0 10px 12px;
`;

export const GnomeInfoList = styled.li`
  text-align: center;
  width: 33%;
  span {
    display: block;
  }
  span:first-child {
    color: ${({ theme }) => theme.secondaryFontColor}
    font-weight: bold;
    font-size: 0.6rem;
    text-transform: uppercase;
    letter-spacing: 1px;
  }
  span:last-child {
    color: ${({ hairColor }) => hairColor || "#f94"};
    font-weight: bold;
    font-size: 0.8rem;
    padding: 1px; 0 10px;

    span.unit {
      display: inline;
      font-size: 0.8rem;
      margin-left: -8px;
    }
  }
`;
