import React from "react";
import { PaginationWrapper, PaginationBtn } from "./PaginationStyled";


const nextButtonValue = "Next";
const previousButtonValue = "Previous";

const Pagination = ({ page, updateGlobalState, maxPages, propsGender, callGender }) => {
  const callGenderLocal = callGender;
  const propsGenderLocal = propsGender;
  return(
  <PaginationWrapper>
    <PaginationBtn
      disableStyle={page <= 0}
      page={page}
      onClick={Pagination.onClick(updateGlobalState, page, propsGenderLocal, callGenderLocal)}
    >
      Previous
    </PaginationBtn>
    <span className="current-page">Page: {page + 1}</span>
    <PaginationBtn
      disableStyle={page >= maxPages}
      page={page}
      onClick={Pagination.onClick(updateGlobalState, page, maxPages, propsGenderLocal, callGenderLocal)}
    >
      Next
    </PaginationBtn>
  </PaginationWrapper>
)};

Pagination.onClick = (updateGlobalState, page, maxPages, propsGenderLocal, callGenderLocal) => (
  event= event.currentTarget
) => {
  event.preventDefault();
  // when page change take scroll to top
  window.scroll(null, 0);
  //console.log(propsGender)
  callGenderLocal(propsGenderLocal)
  // Get the button name
  const btnName = event.currentTarget.textContent.toLowerCase();

  // if btnName is equal tot the nextButton value add a page until find the last page
  if (btnName === nextButtonValue.toLowerCase()) {
    // just add a new page if it is minor than maxPage
    const nextPage = page + 1;
    if (page !== maxPages) {
      updateGlobalState(nextPage);
    }
  } else if (btnName === previousButtonValue.toLowerCase()) {
    // Remove a page until it find the first page
    const previousPage = page - 1;
    if (page > 0) {
      updateGlobalState(previousPage);
    }
  }
};

Pagination.defaultProps = {
  page: false
};

export default Pagination;
