import styled from "styled-components";

const LoadingStyled = styled.div`
  align-items: center;
  background: #fff;
  display: flex;
  justify-content: center;

  & > div.box {
    color: #ccc;
    font-weight: bold;
    max-width: 400px;
    text-align: center;
    width: 100%;
    h1 {
      font-size: 2.3rem;
      margin: 5px 0 12px;
    }
    span {
      font-size: 1.4rem;
    }
  }

  @media screen and (min-width: 400px) {
    & > div.box {
      h1 {
        font-size: 1.5rem;
      }
      span {
        font-size: 1.0rem;
      }
    }
  }
`;

export default LoadingStyled;
