import { CURRENT_PAGE } from "./../actions/types";
import { DIRECTION } from "./../actions/types";

const initialState = {

    currentPage: 0,
    direction: ""

};


function paginationReducer(state = initialState, action) {
  switch (action.type) {
    case CURRENT_PAGE:
      return action.payload;
    case DIRECTION:
      return action.payload;
    default:
      return state;
  }
}
export default paginationReducer;
