// @flow
import React from "react";
import { Helmet } from "react-helmet";
import { connect } from "react-redux";
import DetailsInfo from "./../../components/DetailsInfo/DetailsInfo";
import Card from "./../../components/Card/Card";
import SubTitle from "./../../components/SubTitle/SubTitle";
import EmptyMsg from "./../../components/EmptyMsg/EmptyMsg";
import {
  DetailsRow,
  DetailsWrapper,
  FriendsRow,
  LinkBack
} from "./DetailsStyled";

/*type Props = {
  gnomeURL: object,
  gnomes: Array<object>
};*/

const Details = ({ gnomeURL, gnomes,component }) => {
  // when page change take scroll to top

  console.log("gnomeURL")
  console.log(gnomeURL)
  console.log("gnomes")
  console.log(gnomes)
  console.log("component")
  console.log(component)

  window.scroll(null, 0);
  const selectedGnome =
    gnomes &&
    gnomes.find(
      gnome => gnomeURL === `${gnome.id}-${gnome.name.split(" ").join("-")}`
    );

  const friends =
    selectedGnome && selectedGnome.friends.map(friend => friend.toLowerCase());

  const renderFriends =
    gnomes &&
    gnomes.filter(gnome =>
      friends.some(friend => gnome.name.toLowerCase() === friend)
    );

  return (
    <component />
  );
};

const mapStateToProps = state => ({
  gnomes: state.gnomes
});

export default connect(mapStateToProps)(Details);
